// 
var Path = require('path');
module.exports = function (rootdir, path) {
  path = path.split(/[\\\/]+/);
  var ret = [];
  while(path.length) {
    var name = path.pop();
    if (name[0] === '.') {
      if (name[1] === '.')
        path.pop();
      continue;
    }
    ret.unshift(name);
  }
  ret.unshift(rootdir);
  return Path.join.apply(Path, ret);
};